def question1 (liste)
	nombre_handle = 0
	liste.each do |i|
		nombre_handle += 1
	end
	puts "Il y a #{nombre_handle} handles"
end

def question2 (liste)
	count_char = 0
	count_char_min = 0
	handle_court = ""
	array = {}
	liste.each do |i|
		chars = i.split('')  #découpe les tags en lettres
		chars.each do |c| #boucle des lettres des tags du string
	    	count_char += 1
	    end
	    array[i] = count_char
	    #condition handle le plus court
		if (count_char_min > count_char || count_char_min == 0)
			handle_court = "#{i}"
			count_char_min = count_char
		end
		count_char = 0
	end
	puts "Le handle le plus court est @#{handle_court}, il a #{count_char_min} caractères"	
end

def question3 (liste)
	count_char = 0
	count_cinq_char = 0
	array = {}
	tableau_cinq_char = []
	liste.each do |i|
		chars = i.split('')  #découpe les tags en lettres
		chars.each do |c| #boucle des lettres des tags du string
	    	count_char += 1
	    end
	    array[i] = count_char
	    #condition handle le plus court
		if (count_char == 5)
			tableau_cinq_char << "#{i}"
			count_cinq_char += 1
		end
		count_char = 0
	end
	puts "Il y a #{count_cinq_char} handles à 5 caractères : #{tableau_cinq_char}"
end
	
def question4 (liste)
	tableau_majuscule = []
	count_majuscule = 0

	liste.each do |i|
		chars = i.split('')  #découpe les tags en lettres
		if (chars[0] == chars[0].upcase)
			tableau_majuscule << i
			count_majuscule += 1
		end
	end
	puts "Il y a #{count_majuscule} handles qui commencent par une majuscule"
end

def question5 (liste)
		liste_ordre_alphabetique = liste.sort_by(&:downcase)
		liste_ordre_alphabetique.each do |i|
			i.delete! '@'
			puts "#{i}"  #affiche les tags sans les @
		end
end

def question6 (liste)
		liste_ordre_longueur = liste.sort_by(&:length)
		liste_ordre_longueur.each do |i|
			i.delete! '@'  #enlève les @
		    puts "#{i}"
		end
end

def question7 (liste)
	count_position = 1
	count_epenser = 0
	liste.each do |i|
		if i != "epenser" 
			count_position += 1
			#puts "#{count_position}"
		else
			count_epenser = count_position
		end
	end
	puts "Le handle epenser est en position numéro #{count_epenser}"
end

def question8 (liste) 
		array = {}
		count_char = 0
		count_handle = 0
		erreur = 0

		liste.each do |i|

			i.delete! '@'  #enlève les @
		  	chars = i.split('')  #découpe les tags en lettres

			chars.each do |c| #boucle des lettres des tags du string
		    	count_char += 1
			end

			array[i] = count_char
			count_char = 0
		end
		puts "Les handles contenant combien de caractères voulez-vous ?"
		
		nbchars = gets.chomp.to_i
		while nbchars < 3 || nbchars > 32
			puts "Les handles contenant combien de caractères voulez-vous ?"
			nbchars = gets.chomp.to_i
		end
		puts "Voici les handles avec #{nbchars} caractères :"
		array.each do |handle, length|
			if length == nbchars
				puts "#{handle}"
				erreur += 1
				count_handle += 1
			end
		end
		puts "Il y a #{count_handle} handles avec #{nbchars} caractères :"	
		if (erreur == 0) 
			puts "Il n'y a aucun handle pour ce nombre de caractères"
		end
end

def front
	go = "Y"
	while (go == "Y" || go == "y" )
		
		puts ""
		puts 'Bienvenue dans ce programme, que souhaitez-vous faire ?'
		puts 'Tapez "1" pour la question 1, "2" pour la question 2 etc... '
		puts ""
		puts 'Question 1 : Combien y a-t-il de handle dans cette array ? '
		puts 'Question 2 : Quelle est le handle le plus court de cette liste ? '
		puts 'Question 3 : Combien y-a-t-il de handle contenant 5 caractères ?'
		puts 'Question 4 : Combien commencent par une majuscule (première lettre juste après le @) ?'
		puts 'Question 5 : Trie la liste de handle par ordre alphabétique.'
		puts 'Question 6 : Trie la liste de handle par taille des handle (ordre croissant)'
		puts "Question 7 : Quelle est la position dans l'array de la personne @epenser ?"
		puts 'Question 8 : Sors-moi une répartition des handle par taille de ces derniers'
		puts ""

		mychoice = gets.chomp.to_i

		while ( mychoice < 1 || mychoice > 8 )
			puts "Choisissez un chiffre entre 1 et 8"
			mychoice = gets.chomp.to_i
		end

		liste = initialisation

		if mychoice == 1
			question1 (liste)

		elsif mychoice == 2
			question2 (liste)

		elsif mychoice == 3
			question3 (liste)
		
		elsif mychoice == 4
			question4 (liste)	
		
		elsif mychoice == 5
			question5 (liste)

		elsif mychoice == 6
			question6 (liste)

		elsif mychoice == 7	
			question7 (liste)
		
		elsif mychoice == 8
			question8 (liste)
		end

		puts ""
		puts ("Voulez-vous continuer ? (Y/N)")
		go = gets.chomp
	end
	puts ""
	puts ("Merci et à bientôt")
	exit
end

front